    private void CONFIRMMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_CONFIRMMouseClicked

        String cate ;
        String gen ;
        String car ;
        String inv ;
        String pan ;

        String city;
        String pwd1;
        String pwd2;
        String fn;
        String em;
        String ln;
        int I = 0;
        int i = I++;
        
        

        
        fn = txtF_name.getText();
        ln = txtL_name.getText();
        
        car = txtcar.getText();
        em = txtemail.getText();
        inv = txtinvite.getText();
        pan = txtpan.getText();
        
        pwd1 = password1.getText();
        pwd2 = password2.getText();
        city = cmdcity.getSelectedItem().toString();
        
        if(optu.isSelected())
         city="_";
        
        else if(optd.isSelected())
         city = cmdcity.getSelectedItem().toString();
        
        if(optu.isSelected())
         inv ="_";

        else if(optd.isSelected())
         inv = txtinvite.getText();
       
        if(optu.isSelected())
         pan = txtpan.getText();

        else if(optd.isSelected())
         cate = "Driver";


        if(optmale.isSelected())
         gen= "Male";
        else if(optfemale.isSelected())
         gen= "Female";

        else
         gen = "_";

        if(optu.isSelected())
        cate = "User";

        else if(optd.isSelected())
        cate = "Driver";
        
        else
        cate = "_";
        
    
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection)DriverManager.getConnection("jdbc:mysql://localhost:3306/car_bookings","root","mysql");
            Statement stmt = con.createStatement();
            String sq2 = ("insert into account_reg values('"+i+"','"+fn+"','"+ln+"','"+pwd1+"','"+em+"','"+gen+"','"+pan+"','"+inv+"','"+city+"','"+cate+"','"+car+"');");

            stmt.executeUpdate(sq2);

            if(!pwd1.equals(pwd2)){
            
                JOptionPane.showMessageDialog(null, "Password Do not Match");
            }
            
            else 
            {
                JOptionPane.showMessageDialog(null,"done");
            }
            
           
                
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e.getMessage());

    }//GEN-LAST:event_CONFIRMMouseClicked
    }
