    private void optdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_optdActionPerformed

        if(optd.isSelected())
        {
            txtpan.setEditable(true);
           
            cmdcity.setEnabled(true);
            txtinvite.setEditable(true);
            txtpan.setEditable(true);
            
            txtcar.setEditable(true);
            optmale.setEnabled(true);

        }
        if (txtF_name.equals("") || txtL_name.equals("") || password1.equals("") || txtemail.equals("")  || password2.equals("") || car.equals("") || txtinvite.equals("") || txtpan.equals("") )
        {
            JOptionPane.showMessageDialog(null, "empty fields");
        }
    }//GEN-LAST:event_optdActionPerformed

    private void optuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_optuActionPerformed

        if(optu.isSelected())
        {
            txtpan.setEditable(false);
            
            cmdcity.setEnabled(false);
            txtinvite.setEditable(false);
            txtpan.setEditable(false);
            
            txtcar.setEditable(false);
            optmale.setEnabled(false);
        }

        if (txtF_name.equals("") || txtL_name.equals("") || password1.equals("") || txtemail.equals("")  || password2.equals("") )
        {
            JOptionPane.showMessageDialog(null, "empty fields");
        }

    }//GEN-LAST:event_optuActionPerformed

